INSERT INTO dbo.Products ([ProductID],[ProductName],[GraphicName],[CostPrice],[MSRP],[QtyOnHand],[QtyOnBackOrder],[Description]) VALUES('N_Spice1','Allspice', 'allspice.png', '$2.25', '$2.75', '110',
'0', 'Allspice, also called Jamaica pepper is the dried fruit of the P. dioica plant. The fruits are picked when green and unripe and are traditionally died in the sun. When dry, they are brown and resemble large brown smooth peppercorns. The whole fruits have a longer shelf life than the pwdered product and produce a more aromatic product when freshly ground before use.')

INSERT INTO dbo.Products ([ProductID],[ProductName],[GraphicName],[CostPrice],[MSRP],[QtyOnHand],[QtyOnBackOrder],[Description]) VALUES('N_Spice2','Anise', 'anise.png', '$2.17', '$2.70', '107',
'11', 'Anise, also called aniseed is a flowering plant in the family Apiaceae native to the eastern Mediterranean region and Southwest Asia. Its flavor has similarities with some other spices, such as star anise, fennel, and licorice.')

