INSERT INTO dbo.Products ([ProductID],[ProductName],[GraphicName],[CostPrice],[MSRP],[QtyOnHand],[QtyOnBackOrder],[Description]) VALUES('N_Spice4','Bay Leaf', 'bayleaf.png', '$1.25', '$1.80', '117',
'0', 'Bay leaf, also known as Laurus nobilis, is an aromatic evergreen tree or large shrub with green, glossy leaves, native to the Mediterranean region. It is one of the plants used for bay leaf seasonin in cooking. It is known as bay laurel, sweet bay, bay tree, true laurel, Grecian laurel, or simply laurel.')

INSERT INTO dbo.Products ([ProductID],[ProductName],[GraphicName],[CostPrice],[MSRP],[QtyOnHand],[QtyOnBackOrder],[Description]) VALUES('N_Spice5','Caraway', 'caraway.png', '$0.77', '$0.89', '0',
