INSERT INTO dbo.Products ([ProductID],[ProductName],[GraphicName],[CostPrice],[MSRP],[QtyOnHand],[QtyOnBackOrder],[Description]) VALUES('N_Spice6','Chili Pepper', 'chilipepper.png', '$2.50', '$3.00', '200',
