INSERT INTO dbo.Products ([ProductID],[ProductName],[GraphicName],[CostPrice],[MSRP],[QtyOnHand],[QtyOnBackOrder],[Description]) VALUES('N_Spice7','Chives', 'chives.png', '$0.22', '$0.49', '200',
