INSERT INTO dbo.Products ([ProductID],[ProductName],[GraphicName],[CostPrice],[MSRP],[QtyOnHand],[QtyOnBackOrder],[Description]) VALUES('N_Spice10','Paprika', 'paprika.png', '$2.35', '$2.95', '250',
