INSERT INTO dbo.Products ([ProductID],[ProductName],[GraphicName],[CostPrice],[MSRP],[QtyOnHand],[QtyOnBackOrder],[Description]) VALUES('N_Spice11','Pepper', 'pepper.png', '$2.00', '$2.50', '250',
